export const getLessonsForLang = (langId: string): Lesson[] => {
  return ALL_1500_TOPICS.map((topic, index) => ({
    id: `${langId}-lesson-${index}`,
    title: topic.title,
    category: topic.cat,
    difficulty: index % 3 === 0 ? 'Beginner' : index % 3 === 1 ? 'Intermediate' : 'Advanced',
    progress: Math.floor(Math.random() * 30), // Random starting progress
    icon: topic.icon,
    color: topic.color,
    grammarNotes: topic.grammar,
    vocabulary: topic.vocab,
    quiz: topic.vocab ? topic.vocab.map((v, i) => ({
      id: `q-${index}-${i}`,
      type: 'MULTIPLE_CHOICE',
      prompt: `What is the meaning of "${v}"?`,
      correctAnswer: v, // In a real app this would be translated or defined
      options: [v, 'Incorrect 1', 'Incorrect 2', 'Incorrect 3'].sort(() => Math.random() - 0.5)
    })) : [],
    needsReview: Math.random() > 0.9,
    lastScore: 0,
    rating: 0
  }));
};

export const getGrammarDataForLang = (langId: string): GrammarItem[] => {
  return ALL_1500_TOPICS.map((topic, index) => ({
    id: `${langId}-grammar-${index}`,
    title: topic.title,
    icon: topic.icon,
    explanation: topic.grammar,
    examples: topic.vocab ? topic.vocab.slice(0, 3).map(v => `Example sentence with ${v}.`) : [],
    color: topic.color,
    level: index % 3 === 0 ? 'Beginner' : index % 3 === 1 ? 'Intermediate' : 'Advanced'
  }));
};
