const DEFAULT_TOPICS = [...ALL_1000_TOPICS];
export const getGrammarDataForLang = (langId: string): GrammarItem[] => {
  const base = GRAMMAR_BANK_DATA[langId] || [];
  const genericItems: GrammarItem[] = DEFAULT_TOPICS.map((topic, index) => ({
    id: `g_gen_${langId}_${index}`,
    title: topic.title + " Reference",
    icon: topic.icon,
    explanation: topic.grammar,
    examples: [
      `Learn how to apply "${topic.vocab[0]}" correctly.`,
      `Sentence context: "${topic.grammar.split('.')[0]}"`
    ],
    color: topic.color.replace('bg-', 'bg-').split('-')[0] + '-100',
    level: index < 125 ? 'Beginner' : index < 375 ? 'Intermediate' : 'Advanced'
  }));

  // Force generic light color if parsing failed
  genericItems.forEach(item => {
    if (!item.color.endsWith('-100')) item.color = 'bg-gray-100';
  });

  return [...base, ...genericItems];
};

export const getLessonsForLang = (langId: string): Lesson[] => {
  return DEFAULT_TOPICS.map((t, i) => ({
    id: `l${i}`,
    title: t.title,
    category: t.cat,
    difficulty: i < 125 ? 'Beginner' : i < 375 ? 'Intermediate' : 'Advanced',
    progress: i === 0 ? 100 : 0, 
    icon: t.icon,
    color: t.color,
    grammarNotes: t.grammar,
    vocabulary: t.vocab,
    needsReview: (i % 30 === 0 && i > 0)
  }));
};

export const INITIAL_LESSON_DATA = (): Lesson[] => getLessonsForLang('en-us');

